namespace Baidu\Duer\Botsdk;
class Utils{

    /**
     * 生成token
     * @desc 生成token.  生成一个伪唯一的token
     * @return string
     **/
    public static function genToken(){
        $str = md5(uniqid(mt_rand(), true));
        $uuid  = substr($str,0,8) . '-';
        $uuid .= substr($str,8,4) . '-';
        $uuid .= substr($str,12,4) . '-';
        $uuid .= substr($str,16,4) . '-';
        $uuid .= substr($str,20,12);
        return $uuid;
    }
}
